package core.external.google

/**
 * Object containing the scopes used for accessing Google People API.
 */
object PeopleScopes {

    /**
     * Scope for accessing the user's profile information.
     */
    const val PEOPLE_PROFILE = "https://www.googleapis.com/auth/userinfo.profile"

    /**
     * Scope for accessing the user's email information.
     */
    const val PEOPLE_EMAIL = "https://www.googleapis.com/auth/userinfo.email"

}